    private void generer(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_generer
      if ( BSup < BInf )
      {
	 bornesup.setText("???");
	 nbaleat.setText(" ");
      }
      else {
       nbaleat.setText(String.valueOf((int)((BSup-BInf+1)*Math.random()+BInf)));
      }
    }//GEN-LAST:event_generer

