private void choixBorneInf(java.awt.event.AdjustmentEvent evt) {//GEN-FIRST:event_choixBorneInf
   BInf = bInf.getValue();
   borneinf.setText( String.valueOf( BInf ) );
}//GEN-LAST:event_choixBorneInf

private void choixBorneSup(java.awt.event.AdjustmentEvent evt) {//GEN-FIRST:event_choixBorneSup
   BSup = bSup.getValue();
   bornesup.setText( String.valueOf( BSup ) );    
}//GEN-LAST:event_choixBorneSup
    
